import log from "chalk-logging";
import { API } from "../api";
import { client } from "../../";
import { translate } from "../../modules";

type GuildRoles = "mod" | "hoster" | "member" | string;
type GuildStats = "level" | "games" | "experience" | string;
type GuildChannels = "category" | "verify" | "host" | "hosts" | string;

export class Guild extends API {
  id: string;
  channels: Record<GuildChannels, string | string[]>;
  stats: Record<GuildStats, number | string>;
  roles: Record<GuildRoles,string>;
  open: boolean;
  language: string;
  prefix: string;
  region: string;

  res?: TranslateResponse;
  msg?: DiscordMessage;
  constructor(
    public opt?: apiOptions
  ) {
    super();
    if (opt?.msg) {
      this.msg = opt.msg;
      this.res = translate(this.msg, "guild");
    }
  }
}