import { Socket } from "socket.io-client";

declare global {
  type regions = "NA";
  type switchTeamOptions = "one" | "two" | "spectator";
  
  interface defaultQuery {
    name: string;
    region: string;
    summoner?: string;
    data?: any;
    id?: string;
    team?: switchTeamOptions;
  };
  
  interface LobbySocket extends Socket {
    name?: string,
    region?: regions
  }
}
