type LeagueListDTO = {
  leagueId: string;
  entries: Array<LeagueItemDTO>;
  tier: string;
  name: string;
  queue: string;
}

type LeagueItemDTO = {
  freshBlood: boolean;
  wins: number;
  summonerName: string;
  inactive: boolean;
  veteran: boolean;
  hotStreak: boolean;
  rank: string;
  leaguePoints: number;
  losses: number;
  summonerId: string;
  miniSeries?: MiniSeriesDTO;
}

type LeagueEntryDTO = {
  leagueId?: string;
  summonerId: string;
  summonerName: string;
  queueType?: queue;
  tier?: tier;
  rank: string;
  leaguePoints: number;
  wins: number;
  losses: number;
  hotStreak: boolean;
  veteran: boolean;
  freshBlood: boolean;
  inactive: boolean;
  miniSeries?: MiniSeriesDTO;
}

type MiniSeriesDTO = {
  losses: number;
  progess: string;
  target: number;
  wins: number;
}